<?phpclass SupportedCountry {    public $fullName;    public $countryCode;    public $fromDate;    public $toDate;	public $regions;    public function __construct($fullName, $countryCode, $fromDate, $toDate) {        $this->fullName = $fullName;        $this->countryCode = $countryCode;        $this->fromDate = $fromDate;        $this->toDate = $toDate;		$this->regions = array();    }		public function addRegion($region)	{		$this->regions[] = $region; 	}		public function isRegionSupported($region)	{		for($i = 0; $i < count($this->regions); $i++)		{			if(strcmp($region, $this->regions[$i]) == 0)				return true;		}		return false;	}		public function getArray() {		return array('fullName' => $this->fullName, 'countryCode' => $this->countryCode, 			'fromDate' => array('day' => $this->fromDate->day, 'month' => $this->fromDate->month, 'year' => $this->fromDate->year),			'toDate' => array('day' => $this->toDate->day, 'month' => $this->toDate->month, 'year' => $this->toDate->year),			'regions' => $this->regions);	}}?>